renderpass.code:
float getPixel(vec2 pos, vec2 con) {
    vec2 z = pos;
    float i = 0.f;
    for(i = 0.f; i < 255.f; i++) {
    	z = vec2(z.x * z.x - z.y*z.y, 2.f * z.x * z.y) + con;
        if(length(z) > 2.f) break;
    }
    return i / 255.f;
}

vec3 color(float b) {
	vec3 c;
    c.r = 0.9f * sin(b * 0.45f);
    c.g = sin(b * 1.f);
    c.b = 0.5f * sin(b * 0.5f);
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5f * iResolution.xy)/iResolution.y;
    uv *= 2.f;


    vec2 xy = vec2(iMouse.x / iResolution.x, iMouse.y / iResolution.y);
    xy *= 2.f; xy -= 1.f;
    if( iMouse.z<=0.f )  xy = vec2(cos((iTime + 6.f) * 0.3f) * 0.7f, sin((iTime + 6.f) * 0.3f) * 0.7f);
    float b = getPixel(uv, xy);
    vec3 col = color(b);

    fragColor = vec4(col,1.0);
}
