void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    // sample texture and output to screen
    vec4 buffA = texture(iChannel0, uv);
    vec4 buffB = texture(iChannel1, uv);

    // Time varying pixel color
    vec4 col = vec4(0.0,0.0,0.0,0.0);
    if(buffA.r<0.){
    col.b = -1.0*buffA.r;
    }else{
    col.r = buffA.r;
    }
    // Output to screen
    fragColor = col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 p = fragCoord.xy;
    vec2 res = iResolution.xy;
    vec4 col = vec4(0.0,0.0,0.0,0.0);
    vec4 pc = texture(iChannel0, p/res);
    float v = (pc.r-pc.g);
    vec4 L = -4.0*pc;
    L += texture(iChannel0, (p+vec2( 1, 0))/res);
    L += texture(iChannel0, (p+vec2(-1, 0))/res);
    L += texture(iChannel0, (p+vec2( 0, 1))/res);
    L += texture(iChannel0, (p+vec2( 0, -1))/res);   
    col.r = pc.r+v*0.99+0.5*0.5*L.r; //max wave speed is 0.5 cell per step (0.5^2 in wave eq)
    col.g = pc.r;//store prev state
    fragColor = col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float freq = 10.0;
    vec2 p = fragCoord.xy;
    vec2 res = iResolution.xy;
    vec4 col = texture(iChannel0, p/res);
    if(iMouse.z>0.){
        vec2 m_p = p-iMouse.xy;
        if(length(m_p) < 1.0){
            col.r = 1.0;
            col.g = 0.0;
        }
    }
    if(iFrame%30 == 15){
        vec2 m_p = p-res*0.25;
        if(length(m_p) < 2.0){
            col.r = -1.0;
            col.g = 0.0;
        }
    }
    if(iFrame%30 == 0){
        vec2 m_p = p-res*0.75;
        if(length(m_p) < 2.0){
            col.r = 1.0;
            col.g = 0.0;
        }
    }
    fragColor = col;
}
