float baseRing(vec2 uv, float range, float rate, float size){
    uv *= size;
    float angle = atan(uv.x, uv.y);
    angle = angle/6.28 + 0.5;
    float id = floor(angle * range);
    float fft  = texture(iChannel0, vec2(id / range, rate)).x + 0.4;
    uv /= fft;
    float d = length(uv - vec2(0));
    float m = smoothstep(0.8, 0.65, d) * smoothstep(0.4, 0.65, d);
    return m;
}

float N11(float x) {
    x = fract(sin(x) * 512.5);
    x = fract(x * x);
    return x;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;
    vec3 col =  vec3(0);
    col.r =  baseRing(uv, 10000., 0.3, 1.1) * 0.8;
    col.g =  baseRing(uv, 10000., 0.4, 1.2);
    col.b =  baseRing(uv, 10000., 0.5, 1.3) * 1.2;

    float d = 1.-length(uv);
    float fft = texture(iChannel0, vec2(0.1, 0.3)).x + 0.4;
    col += d * fft*vec3(0.4, 0.88, 1.);

    fragColor = vec4(col,1.0);
}
