vec4 BlurColor (in vec2 Coord, in sampler2D Tex, in float MipBias)
{

	vec2 TexelSize =iResolution.xy;

    vec4  Color = texture(Tex, Coord, 4.);
    Color += texture(Tex, Coord + vec2(TexelSize.x,0.0), 4.);    	
    Color += texture(Tex, Coord + vec2(-TexelSize.x,0.0), 4.);    	
    Color += texture(Tex, Coord + vec2(0.0,TexelSize.y), 4.);    	
    Color += texture(Tex, Coord + vec2(0.0,-TexelSize.y), 4.);    	
    Color += texture(Tex, Coord + vec2(TexelSize.x,TexelSize.y), 4.);    	
    Color += texture(Tex, Coord + vec2(-TexelSize.x,TexelSize.y), 4.);    	
    Color += texture(Tex, Coord + vec2(TexelSize.x,-TexelSize.y), 4.);    	
    Color += texture(Tex, Coord + vec2(-TexelSize.x,-TexelSize.y), 4.);    

    return Color/9.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float time = iTime;

    while(time > 1.0) time -= 1.0;
    time += 0.5;

    vec4 color = texture(iChannel0, uv);

    float strength = 12.0;

    float x = (uv.x + 4.0 ) * (uv.y + 4.0 ) * (time*100.);
	vec4 grain = vec4(mod((mod(x, 13.0) + 1.0) * (mod(x, 123.0) + 1.0), 0.01)-0.005) * strength;


    float Threshold = 0.0;
    float Intensity = 1.0;
    float BlurSize = 4.0;

    vec4 Highlight = clamp(BlurColor(uv, iChannel0, BlurSize)-Threshold,0.0,1.0)*1.0/(1.0-Threshold);

    color.rgb = ((color.rgb - 0.5f) * 1.2 + 0.5f);
    color = 1.0-(1.0-color)*(1.0-Highlight*Intensity); //Screen Blend Mode

    color.b *= 1.2;
    color.r *= 0.9;

	fragColor = color + grain;
    //grain = 1.0 - grain;
	//fragColor = color * grain;
}
