void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 coord = fragCoord/iResolution.xy;  // 0 - 1
    vec3 colour = vec3(0.0);
    vec2 translate = vec2(-0.5, -0.5);
    coord += translate;

    float radius = 0.4;
    float glow = 0.00075;
    float num = 50.f;

    for(int i = 0; i < 40; i++)
    {
        float rad = radians(360.f / num) * float(i);
        colour += glow / length(coord + vec2(radius * cos(rad * (iTime / 8.f)), radius * sin(rad * iTime / 10.f) ));
    }


    // Output to screen
    fragColor = vec4(vec3(colour), 1.0);
}
