//------------------------------------------------
// Shader:  Simple2dZoomRotation.glsl 2020-01-28
//                                    2021-12-16
// Simple example of texture rotation and zooming.
//  mouse.x = shift
//  mouse.y= zoom
//------------------------------------------------

#define DEG 3.1415 / 180.
#define R   iResolution

mat2 rotationMatrix(float angle)  // degrees
{
  angle *= DEG;
  float s=sin(angle), c=cos(angle);
  return mat2(c, -s, s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = (fragCoord - 0.5*R.xy) / R.y;
  float zoom = 1.0 - (iMouse.y-25.) / R.y;
  uv *= zoom * rotationMatrix(22.0 * sin(iTime));
  uv.x -= (iMouse.x - 0.5) / R.x;
  fragColor = texture (iChannel0, uv);
}
