// Back to good old times when playing with an oscilloscope in X/Y mode

#define S smoothstep

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);

    vec2 rp = vec2(cos(iTime*20.5), sin(iTime*20.));
    vec2 gp = vec2(cos(iTime*15.5), sin(iTime*17.));
    vec2 bp = vec2(cos(iTime*17.4), sin(iTime*18.));

    col += vec3(S(.15, .1, distance(uv, rp * .4)), 0., 0.);
    col += vec3(0., S(.15, .1, distance(uv, gp * .4)), 0.);
    col += vec3(0., 0., S(.15, .1, distance(uv, bp * .4)));

    fragColor = vec4(col, 1.0);
}
