#define pi 3.14159265359

const float BLUR_ITER = 15.;

const float speed = 7.;
const float shift_power = 0.6;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ratio = (sin(speed*iTime)*0.5 + 0.5) * step(0., sin(speed*iTime - pi/2.));

    vec2 uv = fragCoord/iResolution.xy;
    float alpha = texture(iChannel0, uv).a;
    vec4 forground = texture(iChannel0, uv);
    vec4 echo = texture(iChannel1, uv);

    forground = mix(forground, echo, echo.a);
    forground.rgb *= 1. + ratio;


    float move = sign(uv.y - 0.5) * ratio * shift_power * sin(20. * iTime);
    uv.x += move;
    uv = mod(uv, 1.);
    vec4 background = texture(iChannel2, uv);


    /////////////////////////
    ///    Motion blur    ///
    /////////////////////////

    uv = fragCoord/iResolution.xy;
    for (float i = BLUR_ITER; i >= 0.; --i) {
        uv.x += 0.1 * move * i / BLUR_ITER;
        background.rgb += texture(iChannel2, uv).rgb;
    }
    background.rgb /= BLUR_ITER + 1.;
    //////////////////////////
    //////////////////////////


    fragColor = mix(background, forground, forground.a);
}

