    vec2 uv = fragCoord/iResolution.xy;
    vec4 forground = texture(iChannel0, uv);

    uv = (uv - 0.5) * 0.93 + 0.5;
    fragColor = texture(iChannel1, uv);
    if (ratio >= 0.95 && forground.a > 0.5)
        fragColor = forground;

    fragColor = mix(fragColor, vec4(fragColor.rgb,0.), 1. - ratio);
}
