void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float lx=distance(uv.x,.5)*20.;
    float ly=distance(.5,uv.y)*20.;
    float dir=atan(uv.x-.5,uv.y-.5)*10.;

    vec3 col = 0.5 + 0.3*cos(iTime+uv.xyy+vec3(0,2,4));
    col+=vec3(.5*cos(lx+dir+iTime),.5*cos(lx+dir+iTime),.5*cos(lx+dir+iTime));
    col+=vec3(.5*sin(ly+dir+iTime),.5*sin(ly+dir+iTime),.5*sin(ly+dir+iTime));

    fragColor = vec4(col,1.0);
}
