float random (in vec2 _st) { return fract(sin(dot(_st.xy, vec2(12.9898,78.233)))* 1121.5453123);} // stolen there : https://thebookofshaders.com/edit.php#10/ikeda-04.frag

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord  / iResolution.xy;

    vec3 col = vec3 (0.);

    float pix = 1.5 / iResolution.y;

    uv.x -=  mod(iTime * .1, 1.);
    vec3 deg = vec3 (length(uv.x) );

    vec3 d = vec3( ( uv.x ) );
    //vec3 d = 
    col = smoothstep(pix*20., 0., abs(d) * random(uv) * 10.) ;;

    col *= 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));


    float feed_a = 0.98 + ((sin(iTime* 5.) + 1.)/2. * 0.02);//can't remember where I stole that :O
    float feed_b = .95 ;
    float feed_c = .95 + ((cos(iTime* 3.) + 1.)/2.) * 0.02;;
    vec3 stack= texture(iChannel0,fragCoord.xy/iResolution.xy).xyz * vec3(feed_a, feed_b,feed_c) * .85;


    fragColor = vec4(stack + col,1.0);    
    //fragColor = vec4(col,1.0);
}
