void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // color0
    vec2 uv00 = (fragCoord + vec2(-3,-3))/iResolution.xy;
    vec2 uv01 = (fragCoord + vec2(-3,-2))/iResolution.xy;
    vec2 uv02 = (fragCoord + vec2(-3,-1))/iResolution.xy;
    vec2 uv03 = (fragCoord + vec2(-3,0))/iResolution.xy;
    vec2 uv10 = (fragCoord + vec2(-2,-3))/iResolution.xy;
    vec2 uv11 = (fragCoord + vec2(-2,-2))/iResolution.xy;
    vec2 uv12 = (fragCoord + vec2(-2,-1))/iResolution.xy;
    vec2 uv13 = (fragCoord + vec2(-2,0))/iResolution.xy;
    vec2 uv20 = (fragCoord + vec2(-1,-3))/iResolution.xy;
    vec2 uv21 = (fragCoord + vec2(-1,-2))/iResolution.xy;
    vec2 uv22 = (fragCoord + vec2(-1,-1))/iResolution.xy;
    vec2 uv23 = (fragCoord + vec2(-1,0))/iResolution.xy;
    vec2 uv30 = (fragCoord + vec2(0,-3))/iResolution.xy;
    vec2 uv31 = (fragCoord + vec2(0,-2))/iResolution.xy;
    vec2 uv32 = (fragCoord + vec2(0,-1))/iResolution.xy;
    vec2 uv33 = (fragCoord + vec2(0,0))/iResolution.xy;

    vec4 color0 = (texture(iChannel0, uv00) +
                 texture(iChannel0, uv01) + 
                 texture(iChannel0, uv02) + 
                 texture(iChannel0, uv03) + 
                 texture(iChannel0, uv10) + 
                 texture(iChannel0, uv11) + 
                 texture(iChannel0, uv12) + 
                 texture(iChannel0, uv13) + 
                 texture(iChannel0, uv20) + 
                 texture(iChannel0, uv21) + 
                 texture(iChannel0, uv22) + 
                 texture(iChannel0, uv23) + 
                 texture(iChannel0, uv30) + 
                 texture(iChannel0, uv31) + 
                 texture(iChannel0, uv32) + 
                 texture(iChannel0, uv33))
        		 / 16.0;

    // color1
    vec2 uv40 = (fragCoord + vec2(1,-3))/iResolution.xy;
    vec2 uv41 = (fragCoord + vec2(1,-2))/iResolution.xy;
    vec2 uv42 = (fragCoord + vec2(1,-1))/iResolution.xy;
    vec2 uv43 = (fragCoord + vec2(1,0))/iResolution.xy;
    vec2 uv50 = (fragCoord + vec2(2,-3))/iResolution.xy;
    vec2 uv51 = (fragCoord + vec2(2,-2))/iResolution.xy;
    vec2 uv52 = (fragCoord + vec2(2,-1))/iResolution.xy;
    vec2 uv53 = (fragCoord + vec2(2,0))/iResolution.xy;
    vec2 uv60 = (fragCoord + vec2(3,-3))/iResolution.xy;
    vec2 uv61 = (fragCoord + vec2(3,-2))/iResolution.xy;
    vec2 uv62 = (fragCoord + vec2(3,-1))/iResolution.xy;
    vec2 uv63 = (fragCoord + vec2(3,0))/iResolution.xy;
    vec2 uv70 = (fragCoord + vec2(4,-3))/iResolution.xy;
    vec2 uv71 = (fragCoord + vec2(4,-2))/iResolution.xy;
    vec2 uv72 = (fragCoord + vec2(4,-1))/iResolution.xy;
    vec2 uv73 = (fragCoord + vec2(4,0))/iResolution.xy;

    vec4 color1 = (texture(iChannel0, uv40) +
                 texture(iChannel0, uv41) + 
                 texture(iChannel0, uv42) + 
                 texture(iChannel0, uv43) + 
                 texture(iChannel0, uv50) + 
                 texture(iChannel0, uv51) + 
                 texture(iChannel0, uv52) + 
                 texture(iChannel0, uv53) + 
                 texture(iChannel0, uv60) + 
                 texture(iChannel0, uv61) + 
                 texture(iChannel0, uv62) + 
                 texture(iChannel0, uv63) + 
                 texture(iChannel0, uv70) + 
                 texture(iChannel0, uv71) + 
                 texture(iChannel0, uv72) + 
                 texture(iChannel0, uv73))
        		 / 16.0;

    // color2
    vec2 uv04 = (fragCoord + vec2(-3,1))/iResolution.xy;
    vec2 uv05 = (fragCoord + vec2(-3,2))/iResolution.xy;
    vec2 uv06 = (fragCoord + vec2(-3,3))/iResolution.xy;
    vec2 uv07 = (fragCoord + vec2(-3,4))/iResolution.xy;
    vec2 uv14 = (fragCoord + vec2(-2,1))/iResolution.xy;
    vec2 uv15 = (fragCoord + vec2(-2,2))/iResolution.xy;
    vec2 uv16 = (fragCoord + vec2(-2,3))/iResolution.xy;
    vec2 uv17 = (fragCoord + vec2(-2,4))/iResolution.xy;
    vec2 uv24 = (fragCoord + vec2(-1,1))/iResolution.xy;
    vec2 uv25 = (fragCoord + vec2(-1,2))/iResolution.xy;
    vec2 uv26 = (fragCoord + vec2(-1,3))/iResolution.xy;
    vec2 uv27 = (fragCoord + vec2(-1,4))/iResolution.xy;
    vec2 uv34 = (fragCoord + vec2(0,1))/iResolution.xy;
    vec2 uv35 = (fragCoord + vec2(0,2))/iResolution.xy;
    vec2 uv36 = (fragCoord + vec2(0,3))/iResolution.xy;
    vec2 uv37 = (fragCoord + vec2(0,4))/iResolution.xy;

    vec4 color2 = (texture(iChannel0, uv04) +
                 texture(iChannel0, uv05) + 
                 texture(iChannel0, uv06) + 
                 texture(iChannel0, uv07) + 
                 texture(iChannel0, uv14) + 
                 texture(iChannel0, uv15) + 
                 texture(iChannel0, uv16) + 
                 texture(iChannel0, uv17) + 
                 texture(iChannel0, uv24) + 
                 texture(iChannel0, uv25) + 
                 texture(iChannel0, uv26) + 
                 texture(iChannel0, uv27) + 
                 texture(iChannel0, uv34) + 
                 texture(iChannel0, uv35) + 
                 texture(iChannel0, uv36) + 
                 texture(iChannel0, uv37))
        		 / 16.0;

    // color3
    vec2 uv44 = (fragCoord + vec2(1,1))/iResolution.xy;
    vec2 uv45 = (fragCoord + vec2(1,2))/iResolution.xy;
    vec2 uv46 = (fragCoord + vec2(1,3))/iResolution.xy;
    vec2 uv47 = (fragCoord + vec2(1,4))/iResolution.xy;
    vec2 uv54 = (fragCoord + vec2(2,1))/iResolution.xy;
    vec2 uv55 = (fragCoord + vec2(2,2))/iResolution.xy;
    vec2 uv56 = (fragCoord + vec2(2,3))/iResolution.xy;
    vec2 uv57 = (fragCoord + vec2(2,4))/iResolution.xy;
    vec2 uv64 = (fragCoord + vec2(3,1))/iResolution.xy;
    vec2 uv65 = (fragCoord + vec2(3,2))/iResolution.xy;
    vec2 uv66 = (fragCoord + vec2(3,3))/iResolution.xy;
    vec2 uv67 = (fragCoord + vec2(3,4))/iResolution.xy;
    vec2 uv74 = (fragCoord + vec2(4,1))/iResolution.xy;
    vec2 uv75 = (fragCoord + vec2(4,2))/iResolution.xy;
    vec2 uv76 = (fragCoord + vec2(4,3))/iResolution.xy;
    vec2 uv77 = (fragCoord + vec2(4,4))/iResolution.xy;

    vec4 color3 = (texture(iChannel0, uv44) +
                 texture(iChannel0, uv45) + 
                 texture(iChannel0, uv46) + 
                 texture(iChannel0, uv47) + 
                 texture(iChannel0, uv54) + 
                 texture(iChannel0, uv55) + 
                 texture(iChannel0, uv56) + 
                 texture(iChannel0, uv57) + 
                 texture(iChannel0, uv64) + 
                 texture(iChannel0, uv65) + 
                 texture(iChannel0, uv66) + 
                 texture(iChannel0, uv67) + 
                 texture(iChannel0, uv74) + 
                 texture(iChannel0, uv75) + 
                 texture(iChannel0, uv76) + 
                 texture(iChannel0, uv77))
        		 / 16.0;

    // Output to screen
    fragColor = (color0+color1+color2+color3)/4.0;
}
