void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    float scale = iResolution.x/iResolution.y;
	vec2 q = uv-vec2(0.5*scale,0.5);

    float radius = 0.2 + 0.02*cos((atan(q.y,q.x))*7.+(iTime*0.5))*cos(iTime);

    vec3 col = vec3(abs(cos((q.y+q.x)*iTime)*cos(iTime)*0.7),0.654,0.3);

    col=col*smoothstep( 2.5/iResolution.y, -2.5/iResolution.y, length(q) - (radius) );

    // Output to screen
    fragColor = vec4(col,1.0);
}
