// Texturing
// Eric Galin

const int Steps = 200;      // Number of steps
const float Epsilon = 0.01; // Marching epsilon

// Primitive functions -----------------------------------------------------------------------------------

// Union
// a : field function of left sub-tree
// b : field function of right sub-tree
float Union(float a, float b){
    return min(a,b);
}
float Union(float a, float b, float c){
    return min(a, min(b, c));
}
float Union(float a, float b, float c, float d){
    return min(a, min(b, min(c, d)));
}
float Union(float a, float b, float c, float d, float e){
    return min(a, min(b, min(c, min(d, e))));
}

// Inter
// a : field function of left sub-tree
// b : field function of right sub-tree
float Inter (float a,float b)
{
    return( max(a,b) );
}
float Inter (float a, float b, float c, float d, float e, float f) {
    return max(a, max(b, max(c, max(d, max(e,f)))));
}

vec3 color01 (vec3 c) {
	return c / 255.0;
}

float sinab (float x, float a, float b) {
	x = sin(x);
    x *= (b - a)/2.0;
    x += a;
    return x;
}

float sin01 (float x) {
	return sinab(x, 0.0, 1.0);
}

// Sphere 
// p : point
// c : center of skeleton
// r : radius
float Sphere(vec3 p, vec3 c,float r)
{
  return length(p-c)-r;
}

// Sphere 
// p : point
// n : Normal of plane
// o : Point on plane
float Plane(vec3 p, vec3 n, vec3 o)
{
    return dot((p-o),n);
}

// cree un cube parallele au plan, selon deux sommets opposés a et b
// p : point
// a, b : sommets opposés l'un l'autre
float Box ( vec3 p, vec3 a, vec3 b ) {
    vec3 x = vec3(1.0, 0.0, 0.0);
  	vec3 y = vec3(0.0, 1.0, 0.0);
  	vec3 z = vec3(0.0, 0.0, 1.0);
    return Inter(
        Plane(p, -x, a),
        Plane(p, -y, a),
        Plane(p, -z, a),
        Plane(p, x, b),
        Plane(p, y, b),
        Plane(p, z, b)
    );
}

// Potential field of the object
// p : point
float object(vec3 p)
{
  float v = Union(
      Noise(p + vec3(1.0, 5.0*iTime, sin(iTime))) + Plane(p, vec3(0.0, 0.0, 1.0), vec3(0.0, 0.0, 0.0))
      , Box(p, vec3( -5.0, -5.0, -2.0), vec3( 5.0, 5.0, 8.0 ))
	  , Box(p, vec3( -7.0, -7.0, 8.0), vec3( 7.0, 7.0, 12.0 ))
      , 0.85*Noise(p) + Sphere(p, vec3(15.0, 0.0, 3.0), 2.5)
      , Sphere(p, vec3(-15.0, 0.0, 3.0), 2.5)
  );
  return v;
}

// Analysis of the scalar field --------------------------------------------------------------------------

// Calculate object normal
// p : point
vec3 ObjectNormal(in vec3 p )
{
  float eps = 0.001;
  vec3 n;
  float v = object(p);
  n.x = object( vec3(p.x+eps, p.y, p.z) ) - v;
  n.y = object( vec3(p.x, p.y+eps, p.z) ) - v;
  n.z = object( vec3(p.x, p.y, p.z+eps) ) - v;
  return normalize(n);
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// e : Maximum distance 
// h : hit
// s : Number of steps
float SphereTrace(vec3 o, vec3 u, float e,out bool h,out int s)
{
  h = false;

    // Start at the origin
    float t=0.0;

  for(int i=0; i<Steps; i++)
  {
    s=i;
    vec3 p = o+t*u;
    float v = object(p);
    // Hit object
      if (v < 0.0)
      {
          s=i;
          h = true;
          break;
      }
      // Move along ray
      t += max(Epsilon,v);
      // Escape marched too far away
      if (t>e)
      {
          break;
      }
  }
  return t;
}

// Lighting ----------------------------------------------------------------------------------------------

// Ambient occlusion
// p : Point
// n : Normal
// a : Number of smaples
float AmbientOcclusion(vec3 p,vec3 n, int a)
{
    if (a==0) { return 1.0; }

	float ao=0.0; 

    for (int i=0;i<a;i++)
    {
 		vec3 d = Cosine(581.123*float(i) , n);

        int s;
        bool h;
        float t=SphereTrace(p,d,10.0,h,s);
        if (!h) {ao+=1.0;}
        else if (t>5.0)
            {
             ao+=1.0;   
            }
    }

    ao/=float(a);
	return ao;
}

// Background color
// d : Ray direction
vec3 background(vec3 d)
{
  return mix(vec3(0.452,0.551,0.995),vec3(0.652,0.697,0.995), d.z*0.5+0.5);
}


// Direct lighting
float Light(vec3 p,vec3 n)
{
   // point light
  const vec3 lp = vec3(5.0, 10.0, 25.0);

  vec3 l = normalize(lp - p);

  // Phong shading
  float diff = clamp(dot(n, l),0.0,1.0);

    bool h;
    int s;
    float t=SphereTrace(p+0.1*n,l,100.0,h,s);
if (!h)
    {
     return diff;
    }
    return 0.0; 
}


// Shading and lighting ---------------------------------------------------------------------------


// Grass
// p : Point
vec3 Grass(in vec3 p) {
    return mix(color01(vec3(111.0, 183.0, 0.0)),
               color01(vec3(195.0, 255.0, 19.0)),
               0.5*Noise(p)
              );
}

// Stone 
// p : Point
vec3 Stone(in vec3 p) {
    return mix(vec3(0.42,0.42,0.4),
               vec3(0.7,0.72,0.7),
               0.5*Noise(p)
              );
}

// Wood 
// p : Point
vec3 Wood(in vec3 p) {
    return mix(color01(vec3(66.0, 45.0, 15.0)),
               color01(vec3(155.0, 113.1, 137.0)),
               sin(p.x + p.y + p.z*2.0)
              );
}

// Metal 
// p : Point
vec3 Metal(in vec3 p) {
    float n1, n2, n3;
    n1 = sqrt( sin(p.x*2.0 + p.y*2.0 + p.z*2.0) );
    n2 = n1 / 2.0;
    n3 = sqrt( sin(p.x + p.y + p.z)*n2 );
    return mix(color01(vec3(150.0, 150.0, 150.0)),
               color01(vec3(255.0, 255.0, 255.0)),
               n3
              );
}

// Lamp 
// p : Point
vec3 Lamp(in vec3 p) {
    return mix(color01(vec3(250.0, 250.0, 150.0)),
               color01(vec3(255.0, 255.0, 250.0)),
               sinab(2.0*iTime, -1.0, 1.0) * fbm(p)
              );
}

// River
// p : Point
// n : Normal 
vec3 River(in vec3 p) {
    return mix(
        color01(vec3(180.0, 180.0, 255.0)),
        color01(vec3(100.0, 100.0, 120.0)),
        sin(p.x + p.y + p.z*0.5 + 8.0*iTime)
    );
}

// Damier
// p : Point
vec3 Damier(in vec3 p, vec3 c1, vec3 c2) {
  	vec3 d = p;
    if (d.x < 0.0) d.x--;
    if (d.y < 0.0) d.y--;
    if (d.z < 0.0) d.z--;
    if ((int(d.x) + int(d.y) + int(d.z))%(2) == 0)
		return c1;
    else
        return c2;
}

float Turbulence(vec3 p){
    float d = sqrt(p.x*p.x+p.y*p.y);
	float t = Noise(p);
    for(float i = 0.5; (i < 0.05); i /= 2.0)
     	t += i + Noise(p/i);
    t /= 2.0;
 	return t; 
}

vec3 Noise3D(vec3 p){
 	return vec3(Noise(p), Noise(p + vec3(165.0,126.0,127.0)), Noise(p + vec3(34.0,242.0,342.0)));
}

vec3 Zebre(vec3 p, vec3 c1, vec3 c2) {
    vec3 q = p + 1.0*Noise3D(p) + 0.5*Noise3D(p/0.5);
    float z = abs(mod(q.z, 0.5) - 0.5)*3.0*Noise(p);
    float l = smoothstep(z, 0.0, 0.02);
    return mix(c1, c2, l);
}

vec3 Marbre(vec3 p) {
    vec3 noir = vec3(0.0, 0.0, 0.0);
    vec3 blanc = vec3(1.0, 1.0, 1.0);
 	 vec3 q = p + 10.0*Turbulence(vec3(Noise(p),Noise(p+vec3(5.2,7.1,2.3)),Noise(p+vec3(4.5,8.7,9.1))));
     return Zebre(q, noir, blanc);
}

// Compute color 0.5*Noise(p)*((sin(iTime)/2.0)+0.5 )
// p : Point
// n : Normal
vec3 Color(in vec3 p,in vec3 n) {
    if (p.z < 0.0)
		return River(p);
    if (p.z > 8.0)
		return Marbre(p);
    if ((p.z >= 0.0) && (p.x > 10.0))
        return Stone(p);
    if ((p.z >= 0.0) && (p.x < -10.0))
        return Grass(p);
    return Damier(p, vec3(1.0, 0.0, 1.0), vec3(0.0, 0.0, 0.0));
}


// Shade
// p : Point
// n : Normal at point
vec3 Shade(vec3 p, vec3 n) {
    vec3 ambient = 0.5 * Color(p,n); 
    vec3 diffuse = 0.35 * Light(p,n) * Color(p,n);
   	vec3 c = ambient+diffuse; 
    c += 0.15 * AmbientOcclusion(p+0.1*n,n,50) * vec3(1.0,1.0,1.0);
    return c;
}


// Picture in picture ------------------------------------------------------------------------------

// Shading according to the number of steps in sphere tracing
// n : Number of steps
vec3 ShadeSteps(int n)
{
   float t=float(n)/(float(Steps-1));
   return 0.5+mix(vec3(0.05,0.05,0.5),vec3(0.65,0.39,0.65),t);
}

// Picture in picture
// pixel : Pixel
// pip : Boolean, true if pixel was in sub-picture zone
vec2 Pip(in vec2 pixel, out bool pip)
{
    // Pixel coordinates
    vec2 p = (-iResolution.xy + 2.0*pixel)/iResolution.y;
   if (pip==true)
   {    
    const float fraction=1.0/4.0;
    // Recompute pixel coordinates in sub-picture
    if ((pixel.x<iResolution.x*fraction) && (pixel.y<iResolution.y*fraction))
    {
        p=(-iResolution.xy*fraction + 2.0*pixel)/(iResolution.y*fraction);
        pip=true;
    }
       else
       {
           pip=false;
       }
   }
   return p;
}


// Image
void mainImage( out vec4 color, in vec2 pxy )
{
    // Picture in picture on
    bool pip=true;

   // Pixel
     vec2 pixel=Pip(pxy, pip);

    // Mouse
    vec2 m=iMouse.xy/iResolution.xy;

    // Camera
    vec3 ro,rd;
    Ray(m,pixel,ro,rd);

  // Trace ray

  // Hit and number of steps
  bool hit;
  int s;

  float t = SphereTrace(ro, rd, 100.0,hit, s);

    // Position 
    vec3 pt = ro + t * rd;

  // Shade background
  vec3 rgb = background(rd);

  if (hit)
  {
    // Compute normal
    vec3 n = ObjectNormal(pt);

    // Shade object with light
    rgb = Shade(pt, n);
  }

  // Uncomment this line to shade image with false colors representing the number of steps
  if (pip==true)
  {
      rgb = ShadeSteps(s); 
  }


  color=vec4(rgb, 1.0);
}

// Texturing and noise ---------------------------------------------------------

// Hashing function
// Returns a random number in [-1,1]
float Hash(float seed)
{
    return fract(sin(seed)*43758.5453 );
}

// Hashing function
// Returns a random number in [-1,1]
// p : Vector in space
float Hash(in vec3 p)  
{
    p  = fract( p*0.3199+0.152 );
	p *= 17.0;
    return fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
}

// Procedural value noise with cubic interpolation
// x : Point 
float 
    Noise(in vec3 p)
{
    vec3 i = floor(p);
    vec3 f = fract(p);

    f = f*f*(3.0-2.0*f);
    // Could use quintic interpolation instead of cubic
    // f = f*f*f*(f*(f*6.0-15.0)+10.0);

    return mix(mix(mix( Hash(i+vec3(0,0,0)), 
                        Hash(i+vec3(1,0,0)),f.x),
                   mix( Hash(i+vec3(0,1,0)), 
                        Hash(i+vec3(1,1,0)),f.x),f.y),
               mix(mix( Hash(i+vec3(0,0,1)), 
                        Hash(i+vec3(1,0,1)),f.x),
                   mix( Hash(i+vec3(0,1,1)), 
                        Hash(i+vec3(1,1,1)),f.x),f.y),f.z);
}

float fbm (vec3 p) {
    float n, k;
    int i;
    k = 1.0;
    for( i = 0; (i < 4); i++ ) {
        k /= 2.0;
   		n += k * Noise(p/k); 
    }
    return n;
}

// Lighting -----------------------------------------------------------------------

// Cosine direction 
// seed : Random seed
//    n : Normal
vec3 Cosine( in float seed, in vec3 n)
{
    float u = Hash( 78.233 + seed);
    float v = Hash( 10.873 + seed);

    // Method by fizzer: http://www.amietia.com/lambertnotangent.html
    float a = 6.2831853 * v;
    u = 2.0 * u - 1.0;
    return normalize( n + vec3(sqrt(1.0-u*u) * vec2(cos(a), sin(a)), u) );
}

// Rotation matrix around z axis
// a : Angle
mat3 Rz(float a)
{
    float sa = sin(a); float ca = cos(a); 
    return mat3(ca,sa,0.0,    -sa,ca,0.0,  0.0,0.0,1.0);
}

// Compute the ray
//      m : Mouse position
//      p : Pixel
// ro, rd : Ray origin and direction
void Ray(in vec2 m, in vec2 p,out vec3 ro,out vec3 rd)
{
    float a = 3.0*3.14*m.x;
   	float le = 3.8;

    ro=vec3( 50.0, 0.0, 20.0 );
    ro*=Rz(3.0*3.14*m.x); 

    vec3 ta = vec3(0.0,0.0,0.0);
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,0.0,1.0) ) );
    vec3 vv = normalize( cross(uu,ww));
	rd = normalize( p.x*uu + p.y*vv + le*ww );
}
