void mainImage( out vec4 fragColor, in vec2 p )
{    
    p = floor(vec2(0.15,0.15)*p);
    if(p.x < N && p.y < N)
    {
    	float id = p.x + p.y*N;
        vec2 i = getID(id);
    	fragColor.xyz = 1.*vec3(1,1,1)*jet_range(getParticle(i.x).z,0.,1.);
    }
}

//particle buffer

void mainImage( out vec4 U, in vec2 pos )
{
    if(pos.x < N && pos.y < N)
    {
        pos = floor(pos);
        //this pixel value
        U = texel(ch0, pos);

        float id = 1.3668*2.*3.14159*(pos.x + pos.y*N)/(N*N);
        if(iFrame < 1)
        {
           // U.xy = size*0.5 + size.x*(vec2(1,0)*cos(id) + vec2(0,1)*sin(id))*0.25;
            U.xy = size*hash22(pos);
            U.zw = hash22(2.*pos);
        }
    }
    else discard;
}

//definitions
#define size iResolution.xy
#define pixel(a, p) texture(a, p/vec2(textureSize(a,0)))
#define texel(a, p) texelFetch(a, ivec2(p), 0)
#define ch0 iChannel0
#define ch1 iChannel1
#define ch2 iChannel2
#define ch3 iChannel3
#define PI 3.14159265

#define N 50.

//hash functions
//https://www.shadertoy.com/view/4djSRW
float hash11(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p) - 0.5;
}

float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}


vec2 hash21(float p)
{
	vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
	p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}

vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}

float nbase(float x)
{
    return mix(hash11(floor(x)),hash11(ceil(x)), fract(x));
}

float fbm(float x, float t)
{
    float a = 1., b = 1., c = 0.;
    for(int i = 0; i < 3; i++)
    {
        c += a*nbase(b*x + t);
        a *= 2.5;
        b *= 4.5;
    }
    return c;
}

//functions
float gauss(vec2 x, float r)
{
    return exp(-pow(length(x)/r,2.));
}
float gauss(float x, float r)
{
    return exp(-pow(x/r,2.));
}


//a rainbow colormap from Matlab
float interpolate(float val, float y0, float x0, float y1, float x1) 
{
    return (val-x0)*(y1-y0)/(x1-x0) + y0;
}

float base(float val) 
{
    if ( val <= -0.75 ) return 0.0;
    else if ( val <= -0.25 ) return interpolate( val, 0.0, -0.75, 1.0, -0.25 );
    else if ( val <= 0.25 ) return 1.0;
    else if ( val <= 0.75 ) return interpolate( val, 1.0, 0.25, 0.0, 0.75 );
    else return 0.0;
}

vec3 jet_colormap(float v)
{
    return vec3(base(v - 0.5),base(v),base(v + 0.5));
}

vec3 jet_range(float v, float a, float b)
{
    return jet_colormap(2.*clamp((v-a)/(b-a),0.,1.) - 1.);
}

//Laplacian operator
vec4 Laplace(sampler2D ch, vec2 p)
{
    vec3 dx = vec3(-1,0.,1);
    return texel(ch, p+dx.xy)+texel(ch, p+dx.yx)+texel(ch, p+dx.zy)+texel(ch, p+dx.yz)-4.*texel(ch, p);
}

//Gradient
vec2 Grad(sampler2D ch, vec2 p)
{
    vec3 dx = vec3(-1,0.,1);
    return vec2(length(texel(ch, p+dx.zy)),length(texel(ch, p+dx.yz))) - length(texel(ch, p));
}


float angle_between(vec2 a,vec2 b)
{
    return atan(a.x*b.y-a.y*b.x,dot(a,b));
}

float sdLine( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

uniform sampler2D fake;

vec4 getParticle(float id)
{
    float y = floor(id/N);
    float x = id - N*y;
    return texel(fake, vec2(x,y));
}

//sorting network

//get particle id from id array
float getID(float id)
{
    id = clamp(id, 0., N*N-1.);
    float y = floor(id/N);
    float x = id - N*y;
    return texel(ch1, vec2(x,y)).x;
}

float just_sorting(float id1, float id2)
{
    vec4 a0 = getParticle(getID(id1));
    vec4 a1 = getParticle(getID(id2));
    if(id2 < id1)
    {
        if(a1.z > a0.z)
        	return getID(id2);
    	else
        	return getID(id1);
    } 
    else
    {
        if(a1.z < a0.z)
        	return getID(id2);
    	else
        	return getID(id1);
    }
}


#define slowdown 1.
void mainImage( out vec4 U, in vec2 pos )
{
    if(pos.x < N && pos.y < N)
    {
        pos = floor(pos);
        //this pixel value
        U = texel(ch1, pos);

        float id = pos.x + pos.y*N;


        if(mod(float(iFrame), slowdown) == 0.)
        {

            //odd even sort
            float oe = floor(mod(float(iFrame)/slowdown,2.)); 
            //pair id
            float pair = abs(floor((id+oe)/2.));
            //left or right on the pair
            float pairid = (id + oe) - 2.*pair; //0 or 1
            //partners id
            float id1 = mod((2.*pairid - 1.) + id, N*N);

            U.y = pairid;

            U.x = just_sorting(id, id1);
        }


        if(iFrame < 1)
        {
            U.x = id;
        }
    }
    else discard;
}
