// Visualizing selected colors from one of IÃ±igo Quilez's color palettes
// See https://www.shadertoy.com/view/ll2GD3


//#define THREAD_INDEX 0.0
#define N_OF_THREADS 6.0


vec3 palette(in float t) {
	return vec3(0.5) + vec3(0.5) * cos(6.28318 * (vec3(t) + vec3(0.00, 0.33, 0.67)));
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;

#ifdef THREAD_INDEX
    float t = THREAD_INDEX / N_OF_THREADS; // color of a specific thread
#else
    float t = floor(N_OF_THREADS * uv.x) / N_OF_THREADS;
#endif

    vec3 			 col = palette(fract(uv.x));
    if (uv.y > 0.33) col = palette(t);
    if (uv.y > 0.67) col = vec3(t);

    col *= smoothstep(0.49, 0.485, abs(fract(3.0 * uv.y) - 0.5));
    fragColor = vec4(col, 1.0);
}
