#define R(a)mat2(cos(a),sin(a),-sin(a),cos(a))
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    float i,g,e,s,l;
    for(i;++i<99.;)
    {
        vec3 r=iResolution,
        p=g*vec3((C.xy-.5*r.xy)/r.y,1);
        p.xy*=R(.8);
        p.xz*=R(.8);
        p+=vec3(1.4,.2,iTime);
        s=3.;
        p=abs(mod(p-1.,2.)-1.)-1.;
        for(int j=0;j++<6;)
            p=1.-abs(p-1.),
            p=p*(l=-1.2/dot(p,p))-.3,
            s*=abs(l);
        g+=e=length(p.xy)/s;
        e<.01?O.xyz+=abs(cos(p+log(s*2.)))/i:p;
    }
}
