#define COLOR_1 vec3(1., 1., 0.)
#define COLOR_2 vec3(0., 1., 1.)

#define HEIGHT_OFFSET_1 0.4
#define HEIGHT_OFFSET_2 0.


vec3 lerp(vec3 col1, vec3 col2, float x) {
    return col1 * (1. - x) + col2 * x;
}



float var1(float h1, float h2, float x) {

    float value = (h2 + (x / 2.)) - (h1 - (x / 2.));

    if (value <= 0.){
        value = 0.;
    } else {
        value = 1.;
    }

    return value;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float tex1 = texture(iChannel0, uv).x + HEIGHT_OFFSET_1;
    float tex2 = texture(iChannel1, uv).x + HEIGHT_OFFSET_2;
    tex1 = clamp(tex1, 0., 1.);
    tex2 = clamp(tex2, 0., 1.);

    float alpha = 0.;

    switch (0) {
        case 0: alpha = var1(tex1, tex2, uv.x);
    }

    vec3 blendedColor = lerp(COLOR_1, COLOR_2, alpha);

    // Output to screen
    fragColor = vec4(0.);

    switch (2) {
        case 0: fragColor = vec4(tex1, tex1, tex1, 1.);
    break;
        case 1: fragColor = vec4(tex2, tex2, tex2, 1.);
    break;
        case 2: fragColor = vec4(blendedColor, 1.);
    break;

    }

}
