// and other to sprout :)  https://twitter.com/CookieDemoparty

// 268 chars
void mainImage( out vec4 F, in vec2 C )
{
    vec2 H = vec2(1.,1.732), 
    U = (4.*C-(F.xy=iResolution.xy))/F.y,
    a = mod(U,H)-H*.5, 
    b = mod(U-H*.5,H)-H*.5;
    U = abs((dot(a,a)<dot(b,b))?a:b);
    F = vec4(
              floor(
                     ( 
                         max(U.x,dot(U,normalize(H)))
                         +sin((floor(iTime)+pow(fract(iTime),9.))*2.09)*.2+.1
                     )*13.
                   )
              /8.
            );
}
