void mainImage(out vec4 o,vec2 u){
    o = vec4(0);
    float samples = 50.;
    for(float i = 0.;i < samples; i++){
        float es = sin(i+1.);
        vec2 n = fract(sin(u.x*es*(u.x+u.y)/100.)*vec2(10.,300.));
        o += texture(iChannel0,vec2(u+(n-.5)*100.)/iResolution.y,0.);
    }
    o /= samples;
    o = mix(o,vec4(fract(sin(u.x*(u.x+u.y)/10.34439)*100.)/100.+.1),.7);
    if((u+u).x>iResolution.x)o = texture(iChannel0,vec2(u)/iResolution.y,0.);
}
