float Circle(vec2 uv, vec2 p, float r, float blur) {
	float d = length(uv-p);
    float c = smoothstep(r, r-blur, d);

    return c;
}

float Smiley(vec2 uv, vec2 p, float size) {
    uv -= p;
    uv /= size;

    float mask = Circle(uv, vec2(0.), .45, .05);

    mask -= Circle(uv, vec2(-.13, .2), .07, .01);
    mask -= Circle(uv, vec2(.13, .2), .07, .01);

    float mouth = Circle(uv, vec2(0., 0.), .3, .02);
    mouth -= Circle(uv, vec2(0., .1), .3, .02);

    mask -= mouth;

    return mask;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -=0.5;
    uv.x *= iResolution.x / iResolution.y;

    vec3 col = vec3(0.);

    float t = iTime;
    vec2 p = vec2(cos(t)*.3, sin(t)*.3);

    float mask = Smiley(uv, p, .3);


    col = vec3(1., 1., 0.)*mask;

    fragColor = vec4(col,1.0);
}
