#define resolution 30.
#define waveFrequency 10.
#define waveSize 0.2
#define simulationSpeed 0.05
#define baseColorIntensity 0.2

vec3 baseColor = vec3(0., 0.4, 0.2);

float circle (vec2 uv, float radius, float blur, vec2 offset){
    float dist = distance(uv, offset); 
    return smoothstep(radius, radius - blur, dist); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    float mask = 0.;
    float multiplicativeInverseResolution = 1. / resolution;
    float t = iTime * simulationSpeed;
    float z = 1.;   
    vec3 floatMask = vec3(0.);

    for(float y = -.5; y < 1.3; y += multiplicativeInverseResolution){
        vec2 uvOffset = uv * z;
        float fadeIn = smoothstep(.5, -0.2, y);
        float fadeOut = smoothstep(-0.5, -0.3, y);
        for (float x = -.5; x < .5; x += multiplicativeInverseResolution){    
            vec2 offset = vec2(x + sin((t + y) * waveFrequency) * waveSize, y + cos((t + x) * waveFrequency) * (waveSize * 1.5));                	
            mask += circle(uvOffset, .3, 0.1, offset * vec2(45.,2.5) + vec2(3,-.8)) * fadeIn * fadeOut;

        }
        z += multiplicativeInverseResolution * 50.;
    }

    baseColor *= baseColorIntensity;
    vec3 color = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    fragColor = vec4(baseColor + color * mask,1.0);
}
