///Creado por FabriceNeyret2 en 2019-06-07 y modificado por jorge flores.p


void mainImage( out vec4 o, vec2 u ) {

    float v, l = 1e5, t;


    t=iTime;

    vec2 R = iResolution.xy, z, z0,
         c = (u-.5*R)/R.y/pow(t,t/20.) - vec2(1.001105,.300717);


    //for (float k = 6.3; k >0.; k -= 6e-3)
    for (float k = 6.3; k >0.; k -= 8e-3)
    { 
        v = dot( z = mat2(z,-z.y,z.x)*z + c , z );
        if ( v< l )  l=v, z0=z;
        if (v > 4. ) break;
    }

    // display either map(last z) or map(shortest z) :


    float tmp;
    tmp=abs(length(z0)-.5);
    o = vec4(z0.x+tmp,z0.y+tmp,tmp,1.0);  



}
