#define ABERATION_SPREAD 0.009f
#define EDGE_THRESH 0.1f
#define INTENSITY 3.0f;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 videoSignal = texture(iChannel0, vec2(uv.x,uv.y));

   	vec3 finalOutput = videoSignal.rgb;

    float sampleOne = videoSignal.r;
    float sampleTwo = texture(iChannel0, vec2(uv.x+ABERATION_SPREAD,uv.y)).r;

    if (abs(sampleOne-sampleTwo) > EDGE_THRESH)
    {
       finalOutput.r += (sampleOne-sampleTwo)*INTENSITY;
    }

    // Output to screen
    fragColor = vec4(finalOutput,1.0);
}
