void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
	vec2 uv = fragCoord/iResolution.xy;

	float t = iTime*1.0;
	// invert the half of the screen movement
    if(uv.x>0.5){
    		t*=-1.0;	
	}
    if(uv.x>0.25){
    	t*=-1.0;
    }
    if(uv.x>0.75){
    	t*=-1.0;
    }

	float demo = sin(t+uv.y*100.0);

    //Mouse input
 	if(uv.x > iMouse.x/1000.0){
 		demo *=20.0;

	}

    vec3 img = texture(iChannel0, uv).rgb;
    vec2 mouse = iMouse.xy/iResolution.xy;

   // foo = mix(img, demo, mouse.x);

	fragColor = vec4(demo);
}
