#define SZYBKOSC_WPIERDOLU 4.f
vec3 wpierdol(vec3 color, vec2 tc)
{
        float czas = ( 0.5 + sin( iTime*SZYBKOSC_WPIERDOLU ) ) / 2.0; 
    vec3 tekstura = texture(iChannel1, tc).xyz;

    return mix( color, tekstura,czas );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    vec3 color = texture( iChannel0, uv ).xyz;

       color = wpierdol(color, uv);

    fragColor = vec4(color,0.f);
}
