/**
 * https://stackoverflow.com/questions/4200224/random-noise-functions-for-glsl
 */
float random(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

/**
 * https://qiita.com/7CIT/items/5113587c82fc544c0a58
 */
float heart(vec2 p){
 return pow(p.x*p.x+p.y*p.y-1.,3.)-p.x*p.x*p.y*p.y*p.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
    uv.y *= iResolution.y / iResolution.x;

    float gridLength;
    vec2 gridCoord;
    if(uv.x > 1.) {
        gridLength = 1. + iTime * .2;
    	gridCoord = floor(uv * gridLength + iTime * 2. * sin(iTime));
    } else {
        gridLength = 100. - (tan(iTime *3.));
    	gridCoord = floor(uv * gridLength - iTime * 1. + tan(iTime * 8.));
    }

    vec2 gridFrag = fract(uv * gridLength);

    vec3 color = vec3(random(gridCoord));

    if(fract(gridCoord.y / 2.) == 0.) {
    	if(color.x > .4 && gridFrag.x > color.x / 4.) {
            color = vec3(.0);
        }
    } else {
    	if(color.x > .4 && gridFrag.y > color.x / 4.) {
            color = vec3(.0);
        }
    }

    color.r = .0;
    color.g = .0;

    if(color.b > .5 + (.2 * .5 + .9) * 1. * ((1. - abs(uv.x * 2. - 1.)) / ((sin((iTime - 3.5) / 2.) * .5 + .5) * 10.))) {
    	color.r += (4. * .3 + .8) * 1. * (1. - uv.y);
    }

    color.r += fract(uv.x) * .2;

    // heart
    float l = 1.-step(0.,heart(vec2(uv.x - .5, uv.y - .25) * (5. - (tan(iTime * 3.) * .5 + .5))));
    //color *= vec3(l) * sin(iTime) - .3;
    color *= vec3(l) - .3;

    fragColor = vec4(color,1.0);
}
