const vec4 frontColor = vec4 (0.75, 0.55, 0.71, 1.0);
const vec4 frontColor2 = vec4 (0.85, 0.65, 0.81, 1.0);
const vec4 bgColor = vec4 (0.48, 0.32, 0.85, 0.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 grad = mix(frontColor, bgColor, abs(cos(iTime * 0.3)));
    float wave =  sin((uv.x * sin(iTime * 0.2)) * 0.35);

    float d = smoothstep(0.,0.4 ,uv.y + wave);

    //versione alternativa
    //float wave =  sin((uv.x * sin(iTime * 0.25)) * 0.5);
    //float d = smoothstep(0.,0.4 ,(uv.y + abs(cos(iTime*0.15) * 0.25)) + wave);


	fragColor = mix(grad, bgColor, d);
}
