// This shader will expose the LCD RGB layout of your display.
// 
// You should see black and white thin lins at opposite sides of the box. 
//
// Layout is as follows:
// * Right black line, left white line - horizontal RGB layout (left to right).
// * Left black line, right white line - horizontal BGR layout (left to right).
// * Bottom black line, Top white line - vertical RGB layout (top to bottom).
// * Top black line, Bottom white line - vertical BGR layout (top to bottom).
//
// REF: http://www.lagom.nl/lcd-test/subpixel.php#subpixel.png

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int w = int(iResolution.x);
    int h = int(iResolution.y);

    int x = int(gl_FragCoord.x);
    int y = int(gl_FragCoord.y);

    vec3 col = vec3(1.0,0.0,0.0);
    if ((x < w/3) || ((w-1-w/3) < x))
        col = vec3(0.0,1.0,1.0);
    if ((y < h/3) || ((h-1-h/3) < y))
        col = vec3(0.0,1.0,1.0);

    fragColor = vec4(col,1.0);
}
