mat2 rotate(float a) {
    float s = sin(a), c = cos(a);
    return mat2(c, s, -s, c);
}


float heart(vec2 st)
{
    // 位置とか形の調整
    st = (st - vec2(0., 0.)) * vec2(2.1, 2.8);

    return pow(st.x, 2.) +
              pow(st.y - sqrt(abs(st.x)), 2.);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float size = 0.01;
     uv -= 0.5; 
    float circleDist = 0.;
     float heratDist = 0.;

    float dist = 0.;
    for(float j = 0.; j < 5.;j++){
        for(float i = 1.; i < 40.; i++){
            vec2 pos = vec2(sin(iTime + i ) * cos(j),sin(2. * iTime +  i ))* cos(j) * 0.3 * sin(iTime);
            circleDist = step(distance(pos, uv),size);
            heratDist = step(heart(uv + pos),size);
            dist += mix(heratDist, circleDist,sin(iTime ));
        }
    }

    uv = vec2(dist,dist);
    uv.x = fract(uv . x * 0.3);


    // Time varying pixel color
   vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    col *= dist;

    // Output to screen
    fragColor = vec4(col,1.0);
}
