float rand(vec2 n)
{
	return fract(sin(dot(n.xy, vec2(12.9898, 78.233)))* 43758.5453);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= iMouse.xy/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    uv *= 40.0;

    uv += vec2(234.0, 6.0*iTime*rand(floor(uv.xx+123.0)));
    vec2 id = floor(uv);
    vec2 gv = fract(uv);
    gv -= 0.5+vec2(sin((id.y+2.0)*(id.x+2.0)+iTime*10.0)*0.1, 0.0);

    vec3 col = vec3(1.0-smoothstep(0.1, sin_n(iTime+id.x*id.y)*0.2+0.1, length(gv)));

    col *= (sin(iTime * 2.0 + id.xyx*0.002 + vec3(0, 2, 4))*0.25+0.75)*rand(id);

    fragColor = vec4(col, 1.0);
}
