float repetitions = 1.7;
int organic = 2;

float HexDist(vec2 p) {
    p = abs(p);

    float c = dot(p, normalize(vec2(1,1.73)));
    c = max(c, p.x);

    return c;
}

vec4 HexCoords(vec2 uv) {
    float pi = 3.14159;
    vec2 r = vec2(1, 1.73);
    vec2 h = r*.5;

    vec2 a = mod(uv, r)-h;
    vec2 b = mod(uv-h, r)-h;

    vec2 gv = dot(a, a) < dot(b,b) ? a : b;
    float x = (atan(gv.x, gv.y)) / pi;
    x *= 3.0;
    x = abs(mod(x-1.0,2.0) - 1.0);
    float y = 1.73*distance(gv, vec2(0.0));
    vec2 id = uv-gv;
    return vec4(x, y, id.x,id.y);
}

vec2 triangleSlice(vec2 uv) {
    vec2 xy = uv;
    float rangeby2 = uv.y / 2.0;
    xy.x = mix(0.5-rangeby2,0.5+rangeby2,uv.x);
    float progress = 2.0*abs(mod(uv.x+0.5,1.0)-0.5);
    for (int i=0;i<organic;i++) {
        progress = sin(progress);
    }

    xy.y =  mix(uv.y,uv.y*1.15, progress); //organic look!
    return xy;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
 {
    vec2 size = iResolution.xy;
    vec2 uv =  (fragCoord.xy - 0.5*size)/size.y;

    uv *= repetitions;
    vec4 hc = HexCoords(uv);

    vec3 c = texture(iChannel0, triangleSlice(hc.xy)).xyz;
    fragColor = vec4(c, 1);
}
