float Circle(vec2 uv, vec2 p, float r, float blur) {
float d = length(uv-p);
float c = smoothstep(r, r-blur, d);
return c;
}
float Band (float t, float start, float end, float blur) {
float step1 = smoothstep(start-blur, start+blur, t);
float step2 = smoothstep(end+blur, end-blur, t);
return step1*step2;
}
float Rect (vec2 uv, float left, float right, float bottom, float top, float blur) {
float band1 = Band(uv.x, left, right, blur);
float band2 = Band(uv.y, bottom, top, blur);
return band1*band2;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
vec2 uv = fragCoord.xy / iResolution.xy; // 0 <> 1.
uv -= .5; // -0.5 <> 0.5
uv.x *= iResolution.x/iResolution.y;
vec3 col = vec3(0.);;
float mask = 0.;
mask = Rect(uv, -0.4, 0.435, -.4, .4, .0001);
if(mask != Rect(uv, -0.4, 0.0, .25, .35, .0001) && mask != Rect(uv, -0.4, -0.29, .0255, .25, .0001) &&
  mask != Rect(uv, -0.4, 0.0, -.075, .025, .0001) && mask != Rect(uv, -0.4, -0.29, -.275, -.075, .0001) &&
  mask != Rect(uv, -0.4, 0.0, -.38, -.275, .0001) && mask != Rect(uv, 0.1, 0.21, -0.38, 0.35, 0.0001) &&
  mask !=  Circle(uv, vec2(0.62, 0.0), 0.42, 0.0001) 
 ){
	mask = .0; 
}
if(mask == Circle(uv, vec2(0.72, 0.0), 0.532, 0.1)){
	mask = .0; 
}        
col = vec3(1., 0.6, 1.)*mask;
fragColor = vec4(col, 1.0);
}
