float random (in vec2 _st) {
    return fract(sin(dot(_st.xy,
                         vec2(12.9898,78.233)))*
        4378.5453123);
}



vec3  line( in vec2 p, in vec2 a, in vec2 b,in float t,in vec3 c )
{
    vec2 pa = p - a;
    vec2 ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    float d = length( pa - ba*h );

    return  smoothstep(t/iResolution.y, 0., d ) * c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = ( fragCoord - iResolution.xy) / iResolution.y;
    vec3 color = vec3(0.0);

    uv *= 10.0;
    uv.y -= iTime;

    vec2 uvi = floor(uv);
    vec2 uvf = 2.0*fract(uv)-1.0;

    vec2 p1 = vec2(-0.5, 0.5);
    vec2 p2 = vec2(0.0, 0.5);
    vec2 p3 = vec2(0.5, 0.5);
    vec2 p4 = vec2(-0.5, 0.0);
    vec2 p5 = vec2(0.0, 0.0);
    vec2 p6 = vec2(0.5, 0.0);
    vec2 p7 = vec2(-0.5, -0.5);
    vec2 p8 = vec2(0.0, -0.5);
    vec2 p9 = vec2(0.5, -0.5);



    vec3 c1 = vec3(1.0);
    float d = iResolution.y*0.12; 
    float onoff = step(random(p1), random(uvi));

    vec3 u1 = smoothstep(0.01, 0.05, line(uvf, p1, p2, d, c1))*step(1.0, mod(random(uvi)*10., 2.0));
    vec3 u2 = smoothstep(0.01, 0.05, line(uvf, p2, p3, d, c1))*step(1.0, mod(random(uvi)*12., 2.0));
    vec3 u3 = smoothstep(0.01, 0.05, line(uvf, p4, p5, d, c1))*step(1.0, mod(random(uvi)*14., 2.0));
    vec3 u4 = smoothstep(0.01, 0.05, line(uvf, p5, p6, d, c1))*step(1.0, mod(random(uvi)*18., 2.0));
    vec3 u5 = smoothstep(0.01, 0.05, line(uvf, p7, p8, d, c1))*step(1.0, mod(random(uvi)*20., 2.0));
    vec3 u6 = smoothstep(0.01, 0.05, line(uvf, p8, p9, d, c1))*step(1.0, mod(random(uvi)*22., 2.0));

    vec3 v1 = smoothstep(0.01, 0.05, line(uvf, p1, p4, d, c1))*step(1.0, mod(random(uvi)*11., 2.0));
    vec3 v2 = smoothstep(0.01, 0.05, line(uvf, p4, p7, d, c1))*step(1.0, mod(random(uvi)*13., 2.0));
    vec3 v3 = smoothstep(0.01, 0.05, line(uvf, p2, p5, d, c1))*step(1.0, mod(random(uvi)*15., 2.0));
    vec3 v4 = smoothstep(0.01, 0.05, line(uvf, p5, p8, d, c1))*step(1.0, mod(random(uvi)*17., 2.0));
    vec3 v5 = smoothstep(0.01, 0.05, line(uvf, p3, p6, d, c1))*step(1.0, mod(random(uvi)*19., 2.0));
    vec3 v6 = smoothstep(0.01, 0.05, line(uvf, p6, p9, d, c1))*step(1.0, mod(random(uvi)*21., 2.0));


    vec3[6] u = vec3 [](u1, u2, u3, u4, u5, u6);
    vec3[6] v = vec3 [](v1, v2, v3, v4, v5, v6);


    vec3 g = vec3 (0.0);
    int n = 6;
    for (int i = 0; i < n; i++) {
    g = max(g, max(u[i], v[i]));
    }

    color = g+vec3(0.1, 0.3, 0.6);

    // Output to screen
    fragColor = vec4(color,1.0);
}
