#define M_PI 3.14159265358979323846

float edge(vec2 a, vec2 b, vec2 c) {
    return (c.x - a.x) * (b.y - a.y) - (c.y - a.y) * (b.x - a.x);
}

bool check(vec2 p) {

    vec2 center = vec2(0.2, 0.2);

    vec2 a = vec2(0.0, 0.3) ;
    vec2 b = vec2(0.3, 0.0);
    vec2 c = vec2(0.0, 0.0);

    float phi = iTime * M_PI;
    float sp = sin(phi);
    float cp = cos(phi);

    vec2 ar = a - center;
    vec2 br = b - center;
    vec2 cr = c - center;

    ar.x = (a.x) * cp - (a.y) * sp;
    ar.y = (a.x) * sp + (a.y) * cp;

    br.x = (b.x) * cp - (b.y) * sp;
    br.y = (b.x) * sp + (b.y) * cp;

    cr.x = (c.x) * cp - (c.y) * sp;
    cr.y = (c.x) * sp + (c.y) * cp;

    ar = ar + center;
    br = br + center;
    cr = cr + center;

    float w0 = edge(br, cr, p);
    float w1 = edge(cr, ar, p);
    float w2 = edge(ar, br, p);

    if(w0 >= 0.0 && w1 >= 0.0 && w2 >= 0.0) {

		return true;
    }
    return false;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = fragCoord/iResolution.xy;
    float aspect = iResolution.x / iResolution.y;
    vec4 col = vec4(1.0, 1.0, 1.0, 1.0);

    vec2 rep = vec2(0.5, 0.5);

    vec2 p = vec2(uv.x * aspect, uv.y);
    p = mod(p, rep);

    if(check(p)) {
		col = vec4(1.0, 0.0, 0.0, 1.0);
    }

    fragColor = col;
}
