//inspired by zeta's mondrian! (https://www.shadertoy.com/view/wtlyzf)

vec3 mintCream = vec3(239./255.,246./255.,238./255.),
     darkGrey = vec3(145./255.,151./255.,174./255.),
     red = vec3(1.0,0.0,0.0),
     blue = vec3(0.0,0.0,1.0),
     black = vec3(12.0/255.,9./255.,16./255.),
     white = vec3(250./255.,250./255.,255./255.),
     yellow = vec3(247./255.,235./255.,100./255.);

vec3 rectMaker(vec2 st,vec2 blstart, vec2 toprend)
{
    vec2 box1 = step(blstart,st); 
    box1 -= step(toprend,st);
    return vec3(box1.x*box1.y);
}

vec3 smallPainter(vec2 st,
                    vec2 blstart, 
                    vec2 toprend)
{
    vec2 box1 = step(vec2(0.3,0.2)+0.03+blstart,st); 
    box1 -= step(vec2(0.7,0.9)-0.03-toprend,st);
    return vec3(box1.x*box1.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //initial box vecs
    vec2 containingBoxStart = vec2(0.3,0.2);
    vec2 containingBoxEnd = vec2(0.7,0.9);

    //make the containing box
    vec3 containingBox = rectMaker(uv,containingBoxStart,containingBoxEnd);

    //make the boxes inside there
    vec3 smallBox = rectMaker(uv, containingBoxStart+0.03,containingBoxEnd-0.03);

    vec3 redBox = smallPainter(uv,vec2(0.0,0.4),vec2(0.2,0.0));
    vec3 topWhiteBox = smallPainter(uv,vec2(0.145,0.4),vec2(0.0,0.0));
    vec3 midWhiteLeft = smallPainter(uv,vec2(0.0,0.25),vec2(0.2,0.25));
    vec3 midWhiteRight = smallPainter(uv,vec2(0.145,0.25),vec2(0.0,0.25));
    vec3 yellowBottomLeft = smallPainter(uv,vec2(0.0,0.0),vec2(0.3,0.4));
    vec3 bottomLeftWhite = smallPainter(uv,vec2(0.045,0.0),vec2(0.2,0.4));
    vec3 bottomBlue = smallPainter(uv,vec2(0.145,0.05),vec2(0.1,0.4));
    vec3 bottomWhite = smallPainter(uv,vec2(0.145,0.0),vec2(0.1,0.6));
    vec3 bottomRightWhite = smallPainter(uv,vec2(0.245,0.0),vec2(0.0,0.4));

    //set the color up with the boxes
    vec3 col = vec3(containingBox);
    col = mix(col,darkGrey,1.0-col);
    col = mix(col,black,smallBox);
    col = mix(col,red,redBox);
    col = mix(col,white,topWhiteBox);
    col = mix(col,white,midWhiteLeft);
    col = mix(col,white,midWhiteRight);
    col = mix(col,yellow,yellowBottomLeft);
    col = mix(col,white,bottomLeftWhite);
    col = mix(col,blue,bottomBlue);
    col = mix(col,white,bottomWhite);
    col = mix(col,white,bottomRightWhite);

    // Output to screen
    fragColor = vec4(col,1.0);
}
