vec2 triangle_wave(vec2 a,float scale){
    return (abs(fract(a*scale)-.5));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col;  
    for(int c=0;c<3;c++){
        float t1 = 5.0;
	    vec2 uv = (fragCoord-iResolution.xy)/iResolution.y/t1;
        uv += vec2(iTime/2.0,iTime/3.0)/t1/2.0;

        float offset = .2;
        float scale = 1.5;
        for(int i=0;i<6;i++)
        {

            uv = triangle_wave(uv.yx+offset*uv.y,scale)+triangle_wave(uv,scale);
            //uv= uv+col.xy;
            uv = triangle_wave(uv+col.xy,scale);

        }
     col[c] = fract((uv.x)-(uv.y));
	}

    fragColor = vec4(vec3(col),1.0);

}
