#define al .5;
#define al_1 .98;

mat2 Rot(float a){
    float s=sin(a), c=cos(a);
    return mat2(c,-s,s,c);
}

vec3 out_rect(vec2 coord, vec2 loc, vec2 size){
    vec2 sw=loc-size/2.;
    vec2 ne=loc+size/2.;
    vec2 pct=smoothstep(sw,sw+.04,coord);
    pct-=smoothstep(ne-.04,ne,coord);

    return vec3(pct.x * pct.y);
}

vec3 cir(vec2 coord, vec2 loc,  float ln){
    float d=1.-distance(coord,loc);
    d=smoothstep(ln*.85,ln,d);
    d*=al;
    return vec3(d+ 0.1*cos(-iTime+coord.yxy+vec3(0,2,4)));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= fragCoord.x/iResolution.x;
    uv.y *= fragCoord.x/iResolution.y;
    for(float i=0.; i<5.;i++){
    uv=abs(uv*2.-1.);
    }

    uv*=.8+.3*sin(iTime*1.2);
    uv*=Rot(3.141592/3.*sin(iTime*.5));//(50.+25.*sin(iTime*.7)));
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    col*=cir(uv,vec2(.5),.3);
    //col*=out_rect(uv,vec2(.5),vec2(.6));
    col+=cir(uv,vec2(.5),.7);


    fragColor = vec4(col,1.0);
}
