mat2 rot(float a){float s=sin(a),c=cos(a);return mat2(c,-s,s,c);}

float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}


float dist(vec3 p){
    return length(vec2(length(p.xy)-.5,p.z))-.2;
}

vec3 norm(vec3 p){
    vec2 e = vec2(.001,0.);
	return normalize(vec3(
    	dist(p+e.xyy)-dist(p-e.xyy),
    	dist(p+e.yxy)-dist(p-e.yxy),
        dist(p+e.yyx)-dist(p-e.yyx)
    ));
}


//vec4 raymarch(vec2 uv){
//}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 p;
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;
    float d=0.,i,j,e=1.;
    vec3 p_;
    for(i=0.;i<99.&&e>.001;i++){
            p=vec3(d*uv,d-3.);
        p_ = p;
        mat2 rotXZ = rot(iTime);
        mat2 rotXY = rot(iTime);
        p.xz*=rotXZ;
        p.xy*=rotXY;
        d+=e=sdRoundBox(p,vec3(1.3),0.);
    }
    fragColor += .4/d;
    fragColor = clamp(fragColor, 0., 1.);
    d=0.;
    e=1.;
    for(j=0.;j<99.&&e>.001;j++){
            p=vec3(d*p_.xy,d-1.);
            //p=mod(p,2.)-1.;
            p.xz*=rot(-iTime);
        d+=e=dist(p);
    }

    fragColor.rgb += clamp(norm(p)*.5+.5, 0., 1.);
}
