#define mirror 0
#define phi 1.61803398875
#define AA = 0.1

float mid (float x){return abs(mod(x-.5,1.0)-.5)*2.0;}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord;
    vec2 res = iResolution.xy;
    uv = ( 2.*uv - res ) / res.y; 

    // View
    float zoom = exp(phi + pow(fract(iTime/(8.*phi)),pow(phi,4.0)) * 16.);
    vec2 offset = vec2(	pow(phi, 2.0), phi);

    // Transform 2D
    uv *= zoom;    
    uv += offset;

    // Draw Function
    float x = uv.x;
    float y = uv.y;
    if(mirror == 1)
    {
      	y = sign(x)*y;
    	x = abs(x);
    }


    float value = mod(pow(x,y) - x,y);   

    // Filtering
    if(value < 0.0) 
    {
    	value = abs(value);   
    }
    else if(value > 0.0) 
    {
       	value = abs(value);
    }

    value = pow(value,0.02);    
    value = smoothstep(0.1, 1.125, value);

    // Composition
    vec3 bg = vec3(.17);
    vec3 col = vec3(value);
    col = col;
    col = max(col, bg);    

    // Output
    fragColor = vec4(col,1.0);
}
