vec2 distortion(vec2 uv)
{
    float angle = 3.14 * (uv.y+1.0)/2.0;
    float offset = 0.3*sin(angle);
    uv.x += offset;
    return uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv = distortion(uv);
    //uv = 0.5 * (uv * 0.5 + 1.0);
	fragColor = texture(iChannel0, uv);
}
