float rand(int n){
    return fract(sin(float(n)+0.1)*9784.8932);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord - 0.5 * iResolution.xy;
	uv = floor(uv/5.0);


    int idx = int(iTime/3.0);
    vec2 a = vec2(2.0*rand(2*idx), 2.0*rand(2*idx+1)); // random
    //vec2 a = vec2(1.0, -1.0)*(iMouse.xy/iResolution.xy*2.0-1.0); // control with mouse

    uv = vec2(uv.x*a.x-uv.y*a.y+1e-5, uv.x*a.y+uv.y*a.x+1e-5);
	uv = floor(uv);
    float col = (mod(uv.x+uv.y, 2.0)<0.5?0.0:1.0);
	fragColor = vec4(vec3(col), 1.0);
}
