// Iain Melvin 2019

#define REFLECT

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
#ifdef REFLECT
	uv=abs(2.0*(uv-0.5));
#endif
    float x,y;
    int nsteps=4;

    x=uv[0]*1.0;
    y=uv[1]*1.0;
    float a = texture(iChannel0, vec2(x,0.1) )[0];
    for (int i=0;i<nsteps;i++){
       float fi=float(i);
       a = a * texture(iChannel0, vec2(x+fi*y*0.2,0.1) )[0];
    }

    //low freq zoom..    
    x = uv[0]*.125;
    y = uv[1]*.125;
    float b = texture(iChannel0, vec2(y,0.1) )[0];
    for (int i=0;i<nsteps;i++){
       float fi=float(i);
       b = b * texture(iChannel0, vec2(x+fi*y*0.2,0.1) )[0];
    }

   	float fft = a + b ; 
	fragColor = vec4( sin(fft*3.141*2.5), sin(fft*3.141*2.0),sin(fft*3.141*1.0),1.0);
}
