float Circle(vec2 uv, vec2 p, float r, float blur){

    float d = length(uv-p);
    float c = smoothstep(r, r-blur, d);// 3 input : d√©but, fin et distance smooth
    return c; 
}

float Band(float t, float start, float end, float blur){
	float step1 = smoothstep(start-blur, start + blur, t);
    float step2 = smoothstep(end+blur, end - blur, t);


    return step1*step2;
}

float Rect(vec2 uv, float left, float right, float bottom, float top, float blur){
	float band1 = Band(uv.x,left, right, blur);
    float band2 = Band(uv.y,bottom, top, blur);

    return band1*band2;
}

float Smiley(vec2 uv, vec2 p, float size){
 	uv -= p; // translating parameter

    uv /= size; //scaling parameter

    float mask = Circle(uv,vec2(0.), .4, 0.01);

    mask -= Circle(uv,vec2(-0.13, .2), .07, 0.01);
    mask -= Circle(uv,vec2(0.13, .2), .07, 0.01);


    float mouth = Circle(uv,vec2(0.), .3, 0.02);
    mouth -= Circle(uv,vec2(0.,0.1), .3, 0.02);

    mask-=mouth;

    return mask;
}
float remap01(float a, float b, float t){
	return (t-a) / (b-a);
}


float remap(float a, float b, float c, float d, float t){
	return remap01(a,b,t) * (d-c) + c;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy; 

    float t = iTime; //temps d'affichage en, bas de la fenetre

    uv -=0.5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 col= vec3(0.);

    float mask = 0.;
        //Smiley(uv, vec2(0.),.5);

    // Output to screen

    //col = vec3(mouth);

    float x = uv.x;

    //float m = -(x-.5)*(x+.5);
    //m = m*m*4.;
    float m = sin(t+x*8.)*.1;
    float y = uv.y-m;

   // x+=y*0.2; //losange

    //mask = Rect(vec2(x,y), -.2+y*0.2,.2-y*0.4,-.3,.3,0.01); //modification forme

    float blur = remap(-.5,.5,0.01,0.25,x);

    blur = pow(blur*4., 3.);
    mask = Rect(vec2(x,y), -.5,.5,-.1,.1,blur);

    col=vec3(1.,1.0,1.0)*mask;

    fragColor = vec4(col,1.0);
}
