float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = texture(iChannel0, uv).rgb;

	float amount = 0.08;
	float angle = 0.02;
	float seed = 0.02;
	float seed_x = 0.02;
	float seed_y = 0.02;
	float distortion_x = 0.5;
	float distortion_y = 0.6;
	float col_s = 0.05;

				vec2 p = uv;
				float xs = floor(gl_FragCoord.x / 0.5);
				float ys = floor(gl_FragCoord.y / 0.5);
				vec4 normal = texture (iChannel1, p*seed*seed);
				//vec4 normal = vec4(0.5,0.5,0.5,0.5);
				if(p.y<distortion_x+col_s && p.y>distortion_x-col_s*seed) {
					if(seed_x>0.){
						p.y = 1. - (p.y + distortion_y);
					}
					else {
						p.y = distortion_y;
					}
				}
				if(p.x<distortion_y+col_s && p.x>distortion_y-col_s*seed) {
					if(seed_y>0.){
						p.x=distortion_x;
					}
					else {
						p.x = 1. - (p.x + distortion_x);
					}
				}
				p.x+=normal.x*seed_x*(seed/5.);
				p.y+=normal.y*seed_y*(seed/5.);
				vec2 offset = amount * vec2( cos(angle), sin(angle));
				vec4 cr = texture(iChannel0, p + offset);
				vec4 cga = texture(iChannel0, p);
				vec4 cb = texture(iChannel0, p - offset);
				fragColor = vec4(cr.r, cga.g, cb.b, cga.a);
				vec4 snow = 200.*amount*vec4(rand(vec2(xs * seed,ys * seed*50.))*0.2);
				fragColor = fragColor+ snow;

}
