   	fragColor = vec4(img, 1.);
}

float rgbToGray(vec3 rgb) {
	const vec3 W = vec3(0.2125, 0.7154, 0.0721);
    return dot(rgb, W);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // mirror reverse the camera input
    vec2 pos = fragCoord/iResolution.xy;
    pos = vec2(1.0 - pos.x, pos.y);

    vec3 cam = texture(iChannel0,pos).xyz;

    vec3 image = texture(iChannel1,fragCoord/iResolution.xy).xyz;

    float camBrightness = rgbToGray(cam);
    float imageBrightness = rgbToGray(image);

    if (camBrightness > imageBrightness) {
	    fragColor = vec4(cam, 1.);
    } else {
	    fragColor = vec4(image, 1.);    
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 filtered = texture(iChannel0,fragCoord/iResolution.xy).xyz;

    // Slow fade
    fragColor = vec4(filtered * 0.999, 1.);

    // Click to clear
    vec4 m = iMouse / iResolution.xxxx;
    if(m.z>0.0 ) {
        fragColor = vec4(0.,0.,0.,1.);
    }
}
