#ifdef GL_ES
precision mediump float;
#endif

vec3 drawRectangle(vec4 bounds, float edgeWidth, vec2 st) {
    vec2 stWidth = edgeWidth / iResolution.xy;
    vec2 relPos = st.xy - bounds.xy;
    vec3 color = vec3(1.0);

    // Check to determine if point is inside rect edge or inner part
    vec2 ch1 = step(bounds.xy, st.xy) - step(bounds.xy + bounds.zw, st.xy); // 1.0 if rect
    vec2 ch2 = step(bounds.xy + stWidth, st.xy) - step(bounds.xy + bounds.zw - stWidth, st.xy); // 1.0 if inside inner rect
    float edge = ch1.x * ch1.y - ch2.x * ch2.y; // 1.0 if inside edge

    // Give edge black color
    color -= edge;

    return color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 st = fragCoord.xy/iResolution.xy;
    vec3 color = vec3(0.0);
    float fade = (sin(iTime * 2.0) + 1.0) * 0.5;

    vec3 red = vec3(0.662, 0.129, 0.137);
    vec3 yellow = vec3(0.996, 0.792, 0.227);
    vec3 blue = vec3(0.0, 0.368, 0.608);

    // Draw lines
    color = drawRectangle(vec4(0.05,0.65,0.2,0.4), 10.0, st);
    color *= drawRectangle(vec4(-0.1,0.65,1.2,0.2), 10.0, st);
    color *= drawRectangle(vec4(-0.1,-0.1,0.35,1.2), 10.0, st);
    color *= drawRectangle(vec4(0.7,-0.1,0.25,1.2), 10.0, st);
    color *= drawRectangle(vec4(0.237,-0.1,0.8,0.2), 10.0, st);

    // Draw colors
    if(st.x < 0.25 && st.y > 0.65) {
    	color *= red * fade;
    }
    if(st.x > 0.95 && st.y > 0.65) {
    	color *= yellow * fade;
    }
    if(st.x > 0.7 && st.y < 0.1) {
    	color *= blue * fade;
    }


    fragColor = vec4(color, 1.0);
}
