//	----HSV to RGB converter
//	This is used just to represent the set colourfully
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

//	====MAIN CODE====
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //	options
    float maxIterations = 100.0;

    //	sets background to black
    fragColor = vec4(0.0);

    //	maps fragCoord.xy to 3 by 2 grid where -2 <= x <= 1 and -1 <= y <= 1
    fragCoord.xy.x = ((fragCoord.xy.x / iResolution.xy.x) * (3.0) - 2.0);
    fragCoord.xy.y = ((fragCoord.xy.y / iResolution.xy.y) * (2.0) - 1.0);

    //	main mandelbrot set code
    vec2 z = fragCoord.xy;
    for(float i = 0.0; i < maxIterations; ++i){
        z = fragCoord.xy + vec2(pow(z.x, 2.0) - pow(z.y, 2.0), 2.0 * z.x * z.y);
        if(length(z) > 2.0){
            fragColor = vec4(hsv2rgb(vec3(1.0 - (i / maxIterations), 1.0, 1.0)), 1.0);
        }
    }
}
