void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0,0,0,0);
    vec2 uv = fragCoord/iResolution.xy; uv -= vec2(.5, .5); uv *= 10.;
    vec2 m = vec2(iMouse.x/iResolution.x, iMouse.y/iResolution.y); m -= vec2(.5, .5); m *= 10.;
    float r = max(iResolution.x, iResolution.y) / min(iResolution.x, iResolution.y);
    if(iResolution.x > iResolution.y) { uv.x *= r; m.x *= r; } else { uv.y *= r; m.y *= r; };

    mat2 B = inverse(mat2(m,vec2(1.,.0)));

    uv = B * uv;

    float X = mod(floor(uv.x),2.0);
    float Y = mod(floor(uv.y),2.0);

    if((X==0.0 && Y==0.0) || (X==1.0 && Y==1.0)) fragColor.x = 0.5;

}
