float drawCircle(vec2 uv, vec2 position, float radius, float blur)
{
    float d = length(uv-position);
    float circle = smoothstep(radius, radius-blur, d);
    return circle;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // Offset
    uv -= 0.5;
    // Scale ratio
    uv.x *= iResolution.x/iResolution.y;

    // Draw background
    vec3 color = vec3(1.,1.,0);

    // Draw a mask
    float mask = drawCircle(uv, vec2(.0), 0.18, 0.03);
    mask -= drawCircle(uv, vec2(0.), 0.13, 0.03);

    mask += drawCircle(uv, vec2(0.35, 0.), 0.15, 0.03);
    mask -= drawCircle(uv, vec2(0.35, 0.), 0.11, 0.03);

    mask += drawCircle(uv, vec2(0.65, 0.), 0.12, 0.03);
    mask -= drawCircle(uv, vec2(0.65, 0.), 0.08, 0.03);

    mask += drawCircle(uv, vec2(-0.35, 0.), 0.15, 0.03);
    mask -= drawCircle(uv, vec2(-0.35, 0.), 0.11, 0.03);

    mask += drawCircle(uv, vec2(-0.65, 0.), 0.12, 0.03);
    mask -= drawCircle(uv, vec2(-0.65, 0.), 0.08, 0.03);

    // Bingo!
    color = vec3(color * mask);

    // Output to screen
    fragColor = vec4(vec3(color),1.0);
}
